<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Superadmin_Model extends MY_Model {        function __construct() {        parent::__construct();    }        public function get_superadmin_list(){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role');        $this->db->from('system_admin AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');             return $this->db->get()->result();            }        public function get_single_superadmin($id){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role');        $this->db->from('system_admin AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');        $this->db->where('SA.id', $id);        return $this->db->get()->row();            }         function duplicate_check($username, $id = null ){                              if($id){            $this->db->where_not_in('id', $id);        }        $this->db->where('username', $username);        return $this->db->get('users')->num_rows();                }               public function get_provinceadmin_list(){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,P.provincialname');        $this->db->from('provincial_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');                $this->db->join('provincial AS P', 'P.provincialid = SA.provincial_id', 'left');             return $this->db->get()->result();            }          public function get_single_provadmin($id){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,P.provincialname');        $this->db->from('provincial_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');                $this->db->join('provincial AS P', 'P.provincialid = SA.provincial_id', 'left');        $this->db->where('SA.id', $id);        return $this->db->get()->row();            }          public function get_zonaladmin_list(){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,P.provincialname');        $this->db->from('zonal_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');          $this->db->join('provincial AS P', 'P.provincialid = SA.provincial_id', 'left');             return $this->db->get()->result();            }         public function get_single_zonaladmin($id){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,P.provincialname,D.districtname,Z.zonename');        $this->db->from('zonal_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');                 $this->db->join('provincial AS P', 'P.provincialid = SA.provincial_id', 'left');                 $this->db->join('district AS D', 'D.id = SA.district_id', 'left');                $this->db->join('zone AS Z', 'Z.zoneid = SA.zonal_id', 'left');        $this->db->where('SA.id', $id);        return $this->db->get()->row();            }            public function fetch_zonal($dist_id) {        $this->db->select('zone.zoneid,zone.zonename');        $this->db->from('zone');        $this->db->where('zone.districtid', $dist_id);        $this->db->order_by('zone.zoneid');        $query = $this->db->get();        return $query->result_array();        // return $section;    }            //Subject                 public function get_subjectadmin_list(){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,SL.sub_name');        $this->db->from('subject_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');                $this->db->join('sub_list AS SL', 'SL.id = SA.subject_id', 'left');             return $this->db->get()->result();            }          public function get_single_subjectadmin($id){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role');        $this->db->from('subject_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');        $this->db->where('SA.id', $id);        return $this->db->get()->row();            }            public function get_listsub() {        $this->db->select()->from('sub_list');        $listhostel = $this->db->get();        return $listhostel->result_array();    }                //Department                   public function get_departmentadmin_list(){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,D.department,SD.subdepartment');        $this->db->from('department_dir AS SA');        $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');                $this->db->join('department AS D', 'D.id = SA.department_id', 'left');        $this->db->join('subdepartment AS SD', 'SD.department_id = D.id', 'left');        return $this->db->get()->result();            }          public function get_single_departmentadmin($id){                $this->db->select('SA.*, U.username, U.role_id, R.name AS role,D.department');        $this->db->from('department_dir AS SA');                $this->db->join('users AS U', 'U.id = SA.user_id', 'left');        $this->db->join('roles AS R', 'R.id = U.role_id', 'left');               $this->db->join('department AS D', 'D.id = SA.department_id', 'left');        $this->db->where('SA.id', $id);        return $this->db->get()->row();            }    }