<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Grade_Model extends MY_Model {        function __construct() {        parent::__construct();    }    public function get_garde_list($province_id = null, $district_id = null, $zonal_id = null, $edu_id = null , $school_id = null){                $this->db->select('G.*, S.school_name');        $this->db->from('grades AS G');        $this->db->join('schools AS S', 'S.id = G.school_id', 'left');        //        if($school_id){////            $this->db->where('G.school_id', $school_id);////        }                if($this->session->userdata('role_id') == ADMIN || $this->session->userdata('role_id') == GUARDIAN || $this->session->userdata('role_id') == STUDENT || $this->session->userdata('role_id') == TEACHER || $this->session->userdata('role_id') == ACCOUNTANT || $this->session->userdata('role_id') == LIBRARIN || $this->session->userdata('role_id') == RECEPTIONIST || $this->session->userdata('role_id') == STAFF || $this->session->userdata('role_id') == PROVINCIAL || $this->session->userdata('role_id') == ZONAL){                   $this->db->where('G.school_id', $this->session->userdata('school_id'));        }                else{        if ($province_id) {            $this->db->where('S.provincial', $province_id);        }//        if ($district_id) {            $this->db->where('S.district_id', $district_id);        }//        if ($zonal_id) {            $this->db->where('S.zonal_id', $zonal_id);        }//        if ($edu_id) {            $this->db->where('S.educational_id', $edu_id);        }                  if ($school_id) {            $this->db->where('S.id', $school_id);        }        }        $this->db->where('S.status', 1);                return $this->db->get()->result();    }           function duplicate_check($field, $school_id, $value, $id = null ){                              if($id){            $this->db->where_not_in('id', $id);        }        $this->db->where($field, $value);        $this->db->where('school_id', $school_id);        return $this->db->get('grades')->num_rows();                }}